import React from "react";
import { updateOrder } from "../api";

const UpdateOrder = ({ orderId, refreshOrders }) => {
  const handleUpdate = () => {
    const newAddress = document.getElementById(`address-${orderId}`).value;
    const newPhone = document.getElementById(`phone-${orderId}`).value;

    updateOrder(orderId, newAddress, newPhone).then(() => {
      refreshOrders();
    });
  };

  return (
    <button onClick={handleUpdate} style={{ background: "#4CAF50", color: "white", padding: "5px 10px" }}>
